import { useState } from "react";
import { useNavigate } from "react-router-dom";
import {toast} from 'react-toastify';

const Register = () =>{
    const [name, nameUpdate] = useState('');
    const [username, usernameUpdate] = useState('');
    const [email, emailUpdate] = useState('');
    const [password, passwordUpdate] = useState('');

    const navigate = useNavigate();

    const registerUsingAPI = (e)=>{
        e.preventDefault();
        if(validate()){
            const data = {'name':name, 'username':username, 'email':email, 'password':password}
            fetch('http://localhost:1731/user/register', {
                method:'POST',
                headers:{'content-type':'application/json'},
                body:JSON.stringify(data)
            }).then((res)=>{
                return res.JSON();
            }).then((resp)=>{
                console.log(resp)
                if (Object.keys(resp).length === 0) {
                    toast.error('Registration failed, invalid credentials');
                }else{
                    toast.success('Success');
                    sessionStorage.setItem('name', resp.name);
                    sessionStorage.setItem('username', resp.username);
                    sessionStorage.setItem('email', resp.email);
                    navigate('/login');
                }
            })
        }
    }

    const validate = () => {
        let result = true;
        if (name === '' || name === null) {
            result = false;
            toast.warning('Please Enter name');
        }
        if (username === '' || username === null) {
            result = false;
            toast.warning('Please Enter username');
        }
        if (email === '' || email === null) {
            result = false;
            toast.warning('Please Enter email');
        }
        if (password === '' || password === null) {
            result = false;
            toast.warning('Please Enter Password');
        }
        return result;
    }

    return(
        <div>
            <form onSubmit={registerUsingAPI}>
                name:<input type="text" value={name} onChange={e=>nameUpdate(e.target.value)}></input>
                username:<input type="text" value={username} onChange={e=>usernameUpdate(e.target.value)}></input>
                email:<input type="email" value={email} onChange={e=>emailUpdate(e.target.value)}></input>
                password:<input type="password" value={password} onChange={e=>passwordUpdate(e.target.value)}></input>
                <button type="submit">register</button>
            </form>
        </div>
    );
}

export default Register;
