import { useState } from "react"
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import './login.css'


const Login = () =>{
    const [identifier, identifierUpdate] = useState('');
    const [password, passwordUpdate] = useState('');
    const navigate = useNavigate();

    const ProceedLoginusingAPI = (e) => {
        e.preventDefault();
        if (validate()) {
            let inputobj={"identifier": identifier,
            "password": password};
            fetch("http://localhost:1731/user/login",{
                method:'POST',
                headers:{'content-type':'application/json'},
                body:JSON.stringify(inputobj)
            }).then((res)=>{
                console.log(res);
                if(res.status === 200){
                    res.json().then(data => {
                        sessionStorage.setItem('username',data.username);
                        sessionStorage.setItem('token', data.token);
                    })
                    if(res.json().then(data => data.isVerificationRequired == true)){
                        navigate('/otp-verify');
                    }else{
                        navigate('/dashboard');
                    }
                }
            })
        }
    }
    const validate = () => {
        let result = true;
        if (identifier === '' || identifier === null) {
            result = false;
            toast.warning('Please Enter Username');
        }
        if (password === '' || password === null) {
            result = false;
            toast.warning('Please Enter Password');
        }
        return result;
    }
    return (
        <div className="container">
	<div className="screen">
		<div className="screen__content">
			<form className="login" onSubmit={ProceedLoginusingAPI}>
				<div className="login__field">
					<i className="login__icon fas fa-user"></i>
					<input type="text" className="login__input" placeholder="User name / Email" value={identifier} onChange={(e)=>identifierUpdate(e.target.value)}></input>
				</div>
				<div className="login__field">
					<i className="login__icon fas fa-lock"></i>
					<input type="password" className="login__input" placeholder="Password" value={password} onChange={(e)=>passwordUpdate(e.target.value)}></input>
				</div>
				<button className="button login__submit">
					<span className="button__text">Log In Now</span>
					<i className="button__icon fas fa-chevron-right"></i>
				</button>				
			</form>
		</div>
		<div className="screen__background">
			<span className="screen__background__shape screen__background__shape4"></span>
			<span className="screen__background__shape screen__background__shape3"></span>		
			<span className="screen__background__shape screen__background__shape2"></span>
			<span className="screen__background__shape screen__background__shape1"></span>
		</div>		
	</div>
</div>
    );
}

export default Login;
