import { useNavigate } from "react-router-dom";

const Otp=()=>{
    const [otp_number, otpUpdate] = useState('');
    const navigate = useNavigate();

    const otpVerification = async(e)=>{
        e.preventDefault();
        let inputObj = {"otp_number":otp_number}
        const fetchapi = await fetch('http://localhost:1731/user/verify-otp',{
            method:'POST',
            headers:{'content-type':'application/json'},
            body:JSON.stringify(inputObj)
        });
        if(fetchapi.status === 200){
            if(!sessionStorage.isOrgId){
                navigate('/org');
            }else{
                navigate('/dashboard');
            }
        }
    }
    return (
        <div className="flex flex-col justify-center items-center h-screen">
          <div>
            {otp.map((digit, index) => (
              <input
                key={index}
                type="text"
                value={digit}
                name="otp_number"
                onChange={(e) => handleChange(index, e.target.value)}
                className="w-8 h-10 text-center border border-gray-300 rounded bg-Otpcolor  ml-2 "
              />
            ))}
          </div>
          <div className="mt-5">
            <button
              onClick={otpVerification}
              className="bg-blue-500 text-white px-3 py-1 rounded-lg"
            >
              {isLoading ? "Sending..." : "Submit"}
            </button>
          </div>
          {isError && <p>Error sending OTP</p>}
          {isSuccess && <p>OTP sent successfully</p>}
        </div>
      );

}

export default Otp;
