import { useState } from "react"
import { Link, useNavigate } from "react-router-dom";
import { toast } from "react-toastify";


const Login = () =>{
    const [identifier, identifierUpdate] = useState('');
    const [password, passwordUpdate] = useState('');
    const usenavigate = useNavigate();

    const ProceedLoginusingAPI = (e) => {
        e.preventDefault();
        if (validate()) {
            let inputobj={"identifier": identifier,
            "password": password};
            fetch("http://localhost:1731/user/login",{
                method:'POST',
                headers:{'content-type':'application/json'},
                body:JSON.stringify(inputobj)
            }).then((res) => {
                return res.json();
            }).then((resp) => {
                console.log(resp)
                if (Object.keys(resp).length === 0) {
                    toast.error('Login failed, invalid credentials');
                }else{
                     toast.success('Success');
                     sessionStorage.setItem('identifier',identifier);
                     sessionStorage.setItem('jwttoken',resp.jwtToken);
                   usenavigate('/')
                }
            }).catch((err) => {
                toast.error('Login Failed due to :' + err.message);
            });
        }
    }
    const validate = () => {
        let result = true;
        if (identifier === '' || identifier === null) {
            result = false;
            toast.warning('Please Enter Username');
        }
        if (password === '' || password === null) {
            result = false;
            toast.warning('Please Enter Password');
        }
        return result;
    }
    return (
        <div className="login-container">
            <h2>Login</h2>
            <form onSubmit={ProceedLoginusingAPI}>
                <input type="text" value={identifier} onChange={e=>identifierUpdate(e.target.value)}></input>
                <input type="password" value={password} onChange={e=>passwordUpdate(e.target.value)}></input>
                <input type="submit" value="Login"></input>
            </form>
        </div>
    );
}

export default Login;
