import { useState } from "react"
import { Link, useNavigate } from "react-router-dom";


const Login = () =>{
    const [identifier, identifierUpdate] = useState('');
    const [password, passwordUpdate] = useState('');
    const navigate = useNavigate();

    const ProceedLoginusingAPI = async (e) => {
        e.preventDefault();
        if (validate()) {
            let inputobj={"identifier": identifier,
            "password": password};
            const fetchobj = await fetch("http://localhost:1731/user/login",{
                method:'POST',
                headers:{'content-type':'application/json'},
                body:JSON.stringify(inputobj)
            });
            const dataVat = await fetchobj.json();
            if(dataVat.isVerificationRequired === true){
                navigate('/otp-validate');
            }else{
                navigate('/dashboard');
            }
        }
    }
    const validate = () => {
        let result = true;
        if (identifier === '' || identifier === null) {
            result = false;
        }
        if (password === '' || password === null) {
            result = false;
        }
        return result;
    }
    return (
        <div>
			<form className="login" onSubmit={ProceedLoginusingAPI}>
				<input type="text" placeholder="User name / Email" value={identifier} onChange={(e)=>identifierUpdate(e.target.value)}></input>
				<input type="password" placeholder="Password" value={password} onChange={(e)=>passwordUpdate(e.target.value)}></input>
				<button>Login</button>			
			</form>
            <Link to={'/register'}><button>Register</button></Link>
		</div>
    );
}

export default Login;
